import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Modules.css";
import useCanvaBehavior from "../useCanvaBehavior";

const Modules = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const course = location.state?.course;

  // ✅ hook controlling scroll, hover, and focus states
  const {
    handleMouseEnter,
    handleMouseLeave,
    focusMode,
    hoverDuration,
    focusedChatIds,
    clickModeActive,
    clickErrorRate,
  } = useCanvaBehavior("main-content");

  if (!course) {
    return (
      <div className="no-course">
        <p>No course selected.</p>
        <button onClick={() => navigate("/canvas/dashboard")}>
          Go Back to Dashboard
        </button>
      </div>
    );
  }

  // Helper to check if this specific item is focused
  const isItemFocused = (id) => focusedChatIds.includes(id);

  return (
    
    <div className="subjects-container">
      <aside className="sidebar">
        <h2>Menu</h2>
        <ul>
          {[
            "Home",
            "Announcements",
            "Discussions",
            "Grades",
            "People",
            "Files",
            "Syllabus",
            "Modules",
          ].map((item) => (
            <li key={item}>{item}</li>
          ))}
        </ul>
      </aside>

      <main className="main-content">
        <h1 className="course-title">{course.title}</h1>

        {/* --- Assignments Section --- */}
        <section className="section">
          <h2>Assignments</h2>
          <div className="item-list">
            {course.assignments.map((assignment, index) => {
              const id = `assign-${index}`;
              const isFocused = focusMode && isItemFocused(id);
              const isHidden = focusMode && !isFocused;

              return (
                <div
                  key={id}
                  className={`item ${isFocused ? "focused-item" : ""} ${
                    isHidden ? "hidden-item" : ""
                  }`}
                  onMouseEnter={() => handleMouseEnter(id)}
                  onMouseLeave={handleMouseLeave}
                >
                  {assignment}
                  {isFocused && hoverDuration < 3 && (
                    <div className="focus-mode-label">
                      Focus Mode Activate
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </section>

        {/* --- Lessons Section --- */}
        <section className="section">
          <h2>Lessons</h2>
          <div className="item-list">
            {course.lessons.map((lesson, index) => {
              const id = `lesson-${index}`;
              const isFocused = focusMode && isItemFocused(id);
              const isHidden = focusMode && !isFocused;

              return (
                <div
                  key={id}
                  className={`item ${isFocused ? "focused-item" : ""} ${
                    isHidden ? "hidden-item" : ""
                  }`}
                  onMouseEnter={() => handleMouseEnter(id)}
                  onMouseLeave={handleMouseLeave}
                >
                  {lesson}
                  {isFocused && hoverDuration < 3 && (
                    <div className="focus-mode-label">
                      Focus Mode Activate
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </section>

        {/* ✅ Behavior Tracking Panel */}
        <div className="tracking-panel">
          <p><strong>Hover Duration:</strong> {hoverDuration}s</p>
          <p><strong>Focus Mode:</strong> {focusMode ? "Active" : "Inactive"}</p>
          <p><strong>Focused Items:</strong> {focusedChatIds.join(", ") || "None"}</p>
          <p>
              Click Error Rate:{" "}
              <span
                className={
                  clickErrorRate >= 15 ? "error-rate-high" : "error-rate-normal"
                }
              >
                {clickErrorRate.toFixed(1)}%
              </span>
            </p>
        </div>
      </main>

      <aside className="right-panel">
        {[
          "View Course Stream",
          "Drop the Course",
          "View Course Calendar",
          "View Course Notification",
        ].map((btn, index) => (
          <button key={index} className="right-button">
            {btn}
          </button>
        ))}
      </aside>
    </div>
  );
};

export default Modules;
