import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./Communities.css";

export default function Communities() {
  const navigate = useNavigate();
  const [focusMode, setFocusMode] = useState(false);
  const [focusedClassIds, setFocusedClassIds] = useState([]);
  const [scrollVelocity, setScrollVelocity] = useState(0);
  const [hoverDuration, setHoverDuration] = useState(0);
  const [action, setAction] = useState("Normal Layout");
  const hoverStartTime = useRef(null);
  const lastScrollY = useRef(0);
  const lastTime = useRef(Date.now());
  const enlargeTimeoutRef = useRef(null);

  const [clickErrorRate, setClickErrorRate] = useState(0);
  const [clickModeActive, setClickModeActive] = useState(false);
  const totalClicksRef = useRef(0);
  const errorClicksRef = useRef(0);
  const clickModeTimeoutRef = useRef(null);

  const classes = [
    { id: 1, title: "Mathematics", teacher: "Prof." },
    { id: 2, title: "Biology", teacher: "Dr." },
    { id: 3, title: "History", teacher: "Mr." },
    { id: 4, title: "Chemistry", teacher: "Ms." },
    { id: 5, title: "Programming", teacher: "Engr." },
    { id: 6, title: "Physics", teacher: "Dr." },
    { id: 7, title: "Statistics", teacher: "Prof." },
  ];

  const saveBehavior = (key, value) => {
    const existing = JSON.parse(localStorage.getItem("userBehaviors")) || [];
    existing.push({ timestamp: new Date().toLocaleString(), key, value });
    localStorage.setItem("userBehaviors", JSON.stringify(existing));
  };

  // -----------------------------------------
  // SCROLL VELOCITY TRACKER (unchanged core)

  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY; // Current scroll position
      const currentTime = Date.now();
      const dy = Math.abs(currentY - lastScrollY.current); // Distance scrolled (px)
      const dt = (currentTime - lastTime.current) / 1000; // Time passed (seconds)

      const velocity = dt > 0 ? dy / dt : 0; // px/s
      setScrollVelocity(velocity);

      // Save last scroll position & time
      lastScrollY.current = currentY;
      lastTime.current = currentTime;

      // If user scrolls slowly (<30px/s) ‚Üí Trigger behavior
      if (velocity < 30 && velocity > 0) {
        setAction("Layout Simplified (Slow Scroll)");
        triggerEnlargeMode();
        // log
        saveBehavior("Scroll Velocity (<30px/s)", velocity.toFixed(2));
      } else {
        if (!clickModeActive && !focusMode) setAction("Normal Scrolling");
      }
    };

    // Initialize
    lastScrollY.current = window.scrollY;
    lastTime.current = Date.now();

    window.addEventListener("scroll", handleScroll, { passive: true });
    return () => window.removeEventListener("scroll", handleScroll);
  }, [clickModeActive, focusMode]); // re-evaluate if modes change

  // ENLARGE MODE (scroll) - keeps existing behavior
  const triggerEnlargeMode = () => {
    document.body.classList.add("enlarge-mode");
    document.querySelector(".class-grid-container")?.classList.add("enlarged");

    clearTimeout(enlargeTimeoutRef.current);
    enlargeTimeoutRef.current = setTimeout(() => {
      document.body.classList.remove("enlarge-mode");
      document.querySelector(".class-grid-container")?.classList.remove("enlarged");
      if (!clickModeActive && !focusMode) setAction("Normal Layout");
    }, 10000);
  };

  // -----------------------------------------
  // HOVER FOCUS MODE (unchanged core)
  // -----------------------------------------
  const handleMouseEnter = (id) => {
    hoverStartTime.current = Date.now();
    const hoverInterval = setInterval(() => {
      const elapsed = ((Date.now() - hoverStartTime.current) / 1000).toFixed(1);
      setHoverDuration(elapsed);
    }, 100);
    window.hoverInterval = hoverInterval;

    setTimeout(() => {
      setAction("Focus Mode Triggered");
      setFocusMode(true);
      setFocusedClassIds((prev) => {
        if (!prev.includes(id)) {
          const updated = [...prev, id];
          saveBehavior("Hover Duration (>3s)", `Focused on classes: ${updated.join(", ")}`);
          return updated;
        }
        return prev;
      });

      clearTimeout(window.focusTimeout);
      window.focusTimeout = setTimeout(() => {
        setFocusMode(false);
        setFocusedClassIds([]);
        if (!clickModeActive) setAction("Normal Layout");
      }, 10000);
    }, 3000);
  };

  const handleMouseLeave = () => {
    clearInterval(window.hoverInterval);
    setHoverDuration(0);
    hoverStartTime.current = null;
    if (!clickModeActive) setAction("Hovering over classes");
  };

  // -----------------------------------------
  // CLICK ERROR RATE TRACKING (NEW)
  // Rule B: error click = click outside .class-grid-container
  // -----------------------------------------
  useEffect(() => {
    const onDocClick = (e) => {
      // increase total clicks
      totalClicksRef.current += 1;

      // determine whether the click happened inside the main grid container
      const insideGrid = !!e.target.closest(".class-grid-container");

      if (!insideGrid) {
        // error click
        errorClicksRef.current += 1;
      }

      // compute rate
      const total = totalClicksRef.current;
      const errors = errorClicksRef.current;
      const rate = total > 0 ? (errors / total) * 100 : 0;
      setClickErrorRate(rate);

      // If rate > 15% -> trigger Click Error Mode
      if (rate > 15 && !clickModeActive) {
        triggerClickErrorMode(rate);
      }

      // keep logging clicks optionally (not every click to avoid noise)
      // you can uncomment to log all clicks:
      // saveBehavior("Click Event", insideGrid ? "Inside grid" : "Outside grid");
    };

    document.addEventListener("click", onDocClick);
    return () => document.removeEventListener("click", onDocClick);
  }, [clickModeActive]); // depend on clickModeActive so we don't re-trigger unexpectedly

  const triggerClickErrorMode = (rate) => {
    // activate click-error mode
    setClickModeActive(true);
    setAction("Click Error Mode Active");
    saveBehavior("Click Error Rate Trigger (>15%)", `${rate.toFixed(1)}%`);

    // visually enlarge main content and block outside clicks via overlay (rendered below)
    const grid = document.querySelector(".class-grid-container");
    grid?.classList.add("click-error-enlarged");

    // reset existing timer
    clearTimeout(clickModeTimeoutRef.current);
    clickModeTimeoutRef.current = setTimeout(() => {
      // revert
      setClickModeActive(false);
      setAction("Normal Layout");
      grid?.classList.remove("click-error-enlarged");

      // after mode ends, reset counters to avoid immediate re-trigger (optional)
      totalClicksRef.current = 0;
      errorClicksRef.current = 0;
      setClickErrorRate(0);
    }, 10000);
  };

  // clean up timers on unmount
  useEffect(() => {
    return () => {
      clearTimeout(enlargeTimeoutRef.current);
      clearTimeout(clickModeTimeoutRef.current);
      clearInterval(window.hoverInterval);
      clearTimeout(window.focusTimeout);
    };
  }, []);

  const handleViewClick = (cls) => {
    navigate("/msteams/communities/activity", { state: { cls } });
  };

  return (
    <>
      {/* Overlay to block clicks outside the grid when clickModeActive */}
      {clickModeActive && <div className="click-error-overlay" />}

      <div className={`class-grid-container ${focusMode ? "focus-active" : ""}`}>
        {classes.map((cls) => (
          <div
            key={cls.id}
            className={`class-card ${
              focusMode && !focusedClassIds.includes(cls.id) ? "hidden-card" : ""
            }`}
            onMouseEnter={() => handleMouseEnter(cls.id)}
            onMouseLeave={handleMouseLeave}
          >
            <div className="class-card-header"></div>
            <div className="class-card-body">
              <p className="class-title">
                Title: <span>{cls.title}</span>
              </p>
              <p className="class-teacher">
                Teacher: <span>{cls.teacher}</span>
              </p>
              <button className="class-view" onClick={() => handleViewClick(cls)}>
                view
              </button>
            </div>
          </div>
        ))}

        {focusMode && (
          <div className="focus-popup">
            üëÅ Focus Mode Enabled ({focusedClassIds.length} focused)
          </div>
        )}

        <div className="tracking-panel">
          <p>
            <strong>Scroll Speed:</strong> {Number(scrollVelocity).toFixed(1)} px/s
          </p>
          <p>
            <strong>Hover Duration:</strong> {hoverDuration > 0 ? `${hoverDuration}s` : "0s"}
          </p>
          <p>
            <strong>Click Error Rate:</strong> {clickErrorRate.toFixed(1)}%
          </p>
          <p>
            <strong>Action:</strong> {action}
          </p>
        </div>
      </div>
    </>
  );
}
