import React, { useEffect, useState, useRef } from "react"; 
import { useNavigate } from "react-router-dom";
import "./Communities.css";

export default function Communities() {
  const navigate = useNavigate(); const [focusMode, setFocusMode] = useState(false); const [focusedClassIds, setFocusedClassIds] = useState([]); const [scrollVelocity, setScrollVelocity] = useState(0); const lastScrollY = useRef(0); const lastTime = useRef(Date.now()); const classes = [ { id: 1, title: "Mathematics", teacher: "Prof." }, { id: 2, title: "Biology", teacher: "Dr." }, { id: 3, title: "History", teacher: "Mr." }, { id: 4, title: "Chemistry", teacher: "Ms." }, { id: 5, title: "Programming", teacher: "Engr." }, { id: 6, title: "Physics", teacher: "Dr." }, { id: 7, title: "Statistics", teacher: "Prof." }, ]; // Utility: Save behavior to localStorage const saveBehavior = (key, value) => { const existing = JSON.parse(localStorage.getItem("userBehaviors")) || []; existing.push({ timestamp: new Date().toLocaleString(), key, value }); localStorage.setItem("userBehaviors", JSON.stringify(existing)); }; // üìå SCROLL VELOCITY TRACKER useEffect(() => { const handleScroll = () => { const currentY = window.scrollY; const currentTime = Date.now(); const dy = Math.abs(currentY - lastScrollY.current); const dt = (currentTime - lastTime.current) / 1000; // seconds const velocity = dy / dt; // px/s setScrollVelocity(velocity); lastScrollY.current = currentY; lastTime.current = currentTime; // ‚úÖ Log slow scroll event if (velocity < 30) { saveBehavior("Scroll Velocity (<30px/s)", velocity.toFixed(2)); triggerEnlargeMode(); } }; window.addEventListener("scroll", handleScroll); return () => window.removeEventListener("scroll", handleScroll); }, []); // üìå ENLARGE MODE (sidebar hide) const triggerEnlargeMode = () => { document.body.classList.add("enlarge-mode"); setTimeout(() => { document.body.classList.remove("enlarge-mode"); }, 10000); // 10 seconds }; // üìå HOVER FOCUS MODE const handleMouseEnter = (id) => { setTimeout(() => { setFocusMode(true); setFocusedClassIds((prev) => { if (!prev.includes(id)) { const updated = [...prev, id]; saveBehavior("Hover Duration (>3s)", Focused on classes: ${updated.join(", ")}); return updated; } return prev; }); // Reset focus mode after 10 seconds clearTimeout(window.focusTimeout); window.focusTimeout = setTimeout(() => { setFocusMode(false); setFocusedClassIds([]); }, 10000); }, 3000); // 3 seconds delay } const handleViewClick = (cls) => { navigate("/msteams/communities/activity", { state: { cls } }); };

  return (
    <div className={`class-grid-container ${focusMode ? "focus-active" : ""}`} >
      {classes.map((cls) => (
      <div
        key={cls.id}
        className={`class-card ${
          focusMode && !focusedClassIds.includes(cls.id) ? "hidden-card" : ""
        }`} onMouseEnter={() => handleMouseEnter(cls.id)}
        onMouseLeave={handleMouseLeave}
      >
        <div className="class-card-header"></div>
        <div className="class-card-body">
          <p className="class-title">Title: <span>{cls.title}</span></p>
          <p className="class-teacher">Teacher: <span>{cls.teacher}</span></p>
          <button className="class-view" onClick={() => handleViewClick(cls)}>view</button>
        </div>
      </div>
    ))}

      {focusMode && (
        <div className="focus-popup">
          üëÅ Focus Mode Enabled
        </div>
      )}
      <div className="tracking-panel">
        <p><strong>Scroll Speed:</strong> {scrollVelocity.toFixed(1)} px/s</p>
        <p><strong>Hover Duration:</strong> {hoverDuration > 0 ? `${hoverDuration}s` : "0s"}</p>
        <p><strong>Action:</strong> {action}</p>
      </div>
    </div>
  );
}
