import { useEffect, useState, useRef, useCallback } from "react";

export default function useUserBehavior(containerClassName, options = {}) {
  const SCROLL_THRESHOLD = options.scrollThreshold || 30;
  const CLICK_ERROR_THRESHOLD = options.clickErrorThreshold || 15;
  const EFFECT_DURATION = options.effectDuration || 10000; // 10 seconds

  const [scrollVelocity, setScrollVelocity] = useState(0);
  const [hoverDuration, setHoverDuration] = useState(0);
  const [clickErrorRate, setClickErrorRate] = useState(0);
  const [action, setAction] = useState("Normal Layout");

  const hoverStartTime = useRef(null);
  const lastScrollY = useRef(0);
  const lastTime = useRef(Date.now());
  const totalClicksRef = useRef(0);
  const errorClicksRef = useRef(0);
  const enlargeTimeoutRef = useRef(null);
  const clickModeTimeoutRef = useRef(null);
  const [clickModeActive, setClickModeActive] = useState(false);

  const triggerUIEffect = useCallback((effectClass) => {
  const container = document.querySelector(`.${containerClassName}`);
  if (!container) return;

  // Apply to BOTH container and body
  document.body.classList.add(effectClass);
  container.classList.add(effectClass);

  clearTimeout(enlargeTimeoutRef.current);
  enlargeTimeoutRef.current = setTimeout(() => {
    document.body.classList.remove(effectClass);
    container.classList.remove(effectClass);
    setAction("Normal Layout");
  }, EFFECT_DURATION);
}, [containerClassName, EFFECT_DURATION]);

  // ✅ Scroll Tracking
  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY;
      const currentTime = Date.now();
      const dy = Math.abs(currentY - lastScrollY.current);
      const dt = (currentTime - lastTime.current) / 1000;

      const velocity = dt > 0 ? dy / dt : 0;
      setScrollVelocity(velocity);

      lastScrollY.current = currentY;
      lastTime.current = currentTime;

      if (velocity < SCROLL_THRESHOLD && velocity > 0) {
        setAction("Slow Scroll Detected");
        triggerUIEffect("enlarge-mode");
      } else {
        setAction("Normal Scrolling");
      }
    };

    window.addEventListener("scroll", handleScroll, { passive: true });
    return () => window.removeEventListener("scroll", handleScroll);
  }, [SCROLL_THRESHOLD, triggerUIEffect]);

  // ✅ Hover Tracking
  const handleMouseEnter = useCallback(() => {
    hoverStartTime.current = Date.now();
    const hoverInterval = setInterval(() => {
      setHoverDuration(((Date.now() - hoverStartTime.current) / 1000).toFixed(1));
    }, 100);
    window.hoverInterval = hoverInterval;
  }, []);

  const handleMouseLeave = useCallback(() => {
    clearInterval(window.hoverInterval);
    setHoverDuration(0);
    hoverStartTime.current = null;
  }, []);

  // ✅ Click Error Detection
  useEffect(() => {
    const handleClick = (e) => {
      totalClicksRef.current += 1;

      const insideContainer = !!e.target.closest(`.${containerClassName}`);
      if (!insideContainer) errorClicksRef.current += 1;

      const rate = (errorClicksRef.current / totalClicksRef.current) * 100;
      setClickErrorRate(rate);

      if (rate > CLICK_ERROR_THRESHOLD) {
        setClickModeActive(true);
        setAction("Click Error Mode");
        triggerUIEffect("click-error-enlarged");
      }
    };

    document.addEventListener("click", handleClick);
    return () => document.removeEventListener("click", handleClick);
  }, [CLICK_ERROR_THRESHOLD, triggerUIEffect, containerClassName]);

  // ✅ Cleanup
  useEffect(() => {
    return () => {
      clearTimeout(enlargeTimeoutRef.current);
      clearTimeout(clickModeTimeoutRef.current);
      clearInterval(window.hoverInterval);
    };
  }, []);

  return {
    scrollVelocity,
    hoverDuration,
    clickErrorRate,
    action,
    handleMouseEnter,
    handleMouseLeave,
  };
}
