import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Communities.css";

export default function Communities() {
  const navigate = useNavigate();
  const containerRef = useRef(null);

  const classes = [
    { id: 1, title: "Mathematics", teacher: "Prof. " },
    { id: 2, title: "Biology", teacher: "Dr. " },
    { id: 3, title: "History", teacher: "Mr. " },
    { id: 4, title: "Chemistry", teacher: "Ms. " },
    { id: 5, title: "Programming", teacher: "Engr. " },
    { id: 6, title: "Physics", teacher: "Dr. " },
    { id: 7, title: "Statistics", teacher: "Prof. " },
  ];

  const handleViewClick = (cls) => {
    navigate("/msteams/communities/activity", { state: { cls } });
  };

  // ---------- localStorage helper ----------
  const updateBehaviorData = (type, details) => {
    const existing = JSON.parse(localStorage.getItem(type)) || {
      triggered: 0,
      history: [],
    };
    const updated = {
      ...existing,
      triggered: (existing.triggered || 0) + 1,
      lastTrigger: new Date().toLocaleString(),
      history: [...existing.history, details],
    };
    localStorage.setItem(type, JSON.stringify(updated));
  };

  // ---------- Scroll velocity (unchanged) ----------
  useEffect(() => {
    let lastScrollTop = 0;
    let lastTime = Date.now();
    const sidebar = document.querySelector(".ms-sidebar");
    const msContent = document.querySelector(".ms-content");

    const handleScroll = () => {
      const ref = containerRef.current;
      if (!ref) return;
      const currentScrollTop = ref.scrollTop;
      const currentTime = Date.now();
      const deltaY = Math.abs(currentScrollTop - lastScrollTop);
      const deltaT = (currentTime - lastTime) / 1000 || 0.001;
      const velocity = deltaY / deltaT;

      lastScrollTop = currentScrollTop;
      lastTime = currentTime;

      if (velocity < 30 && sidebar && msContent) {
        // trigger
        sidebar.classList.add("hidden");
        msContent.classList.add("expanded");
        updateBehaviorData("scrollBehavior", { velocity });

        setTimeout(() => {
          sidebar.classList.remove("hidden");
          msContent.classList.remove("expanded");
        }, 10000);
      }
    };

    const ref = containerRef.current;
    ref?.addEventListener("scroll", handleScroll);
    return () => ref?.removeEventListener("scroll", handleScroll);
  }, []);

  // ---------- Click error rate (unchanged) ----------
  useEffect(() => {
    let totalClicks = 0;
    let invalidClicks = 0;

    const handleClick = (e) => {
      totalClicks++;
      const isValidClick = e.target.closest(".class-card") || e.target.closest(".class-view");
      if (!isValidClick) invalidClicks++;

      const errorRate = (invalidClicks / totalClicks) * 100;
      if (errorRate > 15) {
        updateBehaviorData("clickBehavior", { totalClicks, invalidClicks, errorRate });
        const container = document.querySelector(".class-grid-container");
        if (container) {
          container.classList.add("enlarged");
          // block outside clicks by overlay (we add and remove class; overlay is rendered only for hover focus, but here we use enlarged class to style)
          setTimeout(() => container.classList.remove("enlarged"), 10000);
        }
        // reset counters to avoid repeated immediate retriggers (optional)
        totalClicks = 0;
        invalidClicks = 0;
      }
    };

    document.addEventListener("click", handleClick);
    return () => document.removeEventListener("click", handleClick);
  }, []);

  // ---------- Hover focus behavior (NEW) ----------
  // use state to track focused card and whether focus mode is active
  const [focusedCard, setFocusedCard] = useState(null);
  const hoverTimersRef = useRef({}); // store per-card timers

  // start hover timer on enter
  const handleMouseEnter = (id) => {
    // avoid re-triggering if already focused
    if (focusedCard) return;
    clearTimeout(hoverTimersRef.current[id]);
    hoverTimersRef.current[id] = setTimeout(() => {
      // activate focus mode
      setFocusedCard(id);
      updateBehaviorData("hoverBehavior", { cardId: id, activatedAt: new Date().toLocaleString() });

      // auto-clear focus after 10s
      hoverTimersRef.current[`clear_${id}`] = setTimeout(() => {
        setFocusedCard(null);
      }, 10000);
    }, 3000); // 3 seconds threshold
  };

  // cancel timer on leave
  const handleMouseLeave = (id) => {
    clearTimeout(hoverTimersRef.current[id]);
  };

  // When focus is active, prevent body scroll (optional)
  useEffect(() => {
    if (focusedCard) {
      document.body.classList.add("focus-active");
    } else {
      document.body.classList.remove("focus-active");
    }
  }, [focusedCard]);

  return (
    <>
      {/* Focus overlay + top-popup rendered when focusedCard is set */}
      {focusedCard && (
        <>
          {/* top popup */}
          <div className="focus-popup">
            <div className="focus-popup-inner">
              Focus mode enabled â€” only the selected class is visible for 10 seconds
            </div>
          </div>

          {/* overlay blocks clicks outside the focused card (z-index lower than focused card) */}
          <div className="focus-overlay" aria-hidden="true"></div>
        </>
      )}

      <div
        ref={containerRef}
        className={`class-grid-container ${focusedCard ? "focus-active" : ""}`}
      >
        {classes.map((cls) => {
          const isFocused = focusedCard === cls.id;
          return (
            <div
              key={cls.id}
              data-id={cls.id}
              className={`class-card ${isFocused ? "focused" : ""}`}
              onMouseEnter={() => handleMouseEnter(cls.id)}
              onMouseLeave={() => handleMouseLeave(cls.id)}
              // ensure focused card is above overlay and interactive
              style={isFocused ? { zIndex: 9999 } : {}}
            >
              <div className="class-card-header"></div>
              <div className="class-card-body">
                <p className="class-title">
                  Title: <span>{cls.title}</span>
                </p>
                <p className="class-teacher">
                  Teacher: <span>{cls.teacher}</span>
                </p>
                <button className="class-view" onClick={() => handleViewClick(cls)}>
                  view
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </>
  );
}
