// MsProfile.jsx
import React, { useEffect, useState } from "react";
import "./MsProfile.css";

export default function MsProfile() {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    // pull histories for all three behaviors
    const scrollData = JSON.parse(localStorage.getItem("scrollBehavior"))?.history || [];
    const hoverData = JSON.parse(localStorage.getItem("hoverBehavior"))?.history || [];
    const clickData = JSON.parse(localStorage.getItem("clickBehavior"))?.history || [];

    // figure out the max number of rows among them
    const maxLength = Math.max(scrollData.length, hoverData.length, clickData.length);

    // align all metrics by index
    const aligned = Array.from({ length: maxLength }, (_, i) => ({
      scroll: scrollData[i]?.velocity?.toFixed?.(1) || "—",
      hover: hoverData[i]
        ? hoverData[i].duration
          ? hoverData[i].duration.toFixed(1)
          : "3+" // hoverBehavior in Communities currently only stores cardId, so we show "3+"
        : "—",
      click: clickData[i]?.errorRate?.toFixed?.(1) || "—",
    }));

    setRows(aligned.reverse()); // show latest first
  }, []);

  return (
    <div className="profile-container">
      <h2>User Behavior Metrics</h2>
      <table className="behavior-table">
        <thead>
          <tr>
            <th>Scroll Velocity (＜30px/s)</th>
            <th>Hover Duration (＞3s)</th>
            <th>Click Error Rate (＞15%)</th>
          </tr>
        </thead>
        <tbody>
          {rows.length === 0 ? (
            <tr>
              <td colSpan="3" style={{ textAlign: "center", opacity: 0.7 }}>
                No behavior data recorded yet
              </td>
            </tr>
          ) : (
            rows.map((row, i) => (
              <tr key={i}>
                <td>{row.scroll}</td>
                <td>{row.hover}</td>
                <td>{row.click}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
