import React from 'react' 
import './Home.css'

export default function Home() {
  const classes = [
    "IAS - CSB3 2ND SEMESTER 2025",
    "THESIS | 2025",
    "PROGRAMMING LANGUAGE",
    "Math Elective",
    "Web Development",
    "Data Structures",
  ];

  const tasks = [
    { title: "Submit Act. 2", date: "May 25" },
    { title: "Read Chapter 3", date: "May 26" },
  ];

  const handleView = (index) => {
    navigate(`/class/${index}`);
  };

  return (
    <div className="home-contents">
      <div className="classes">
        {classes.map((cls, index) => (
          <div className="class-box" key={index}>
            <h2>{cls}</h2>
            <button onClick={() => handleView(index)}>View</button>
          </div>
        ))}
      </div>

      <div className="announcements">
        <div className='reminder-box'>
          <h2>Upcoming Tasks</h2>
          <ul className="task-list">
            {tasks.map((task, index) => (
              <li key={index}>
                <input type="checkbox" />
                <span>{task.title} - {task.date}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
