import React from "react";
import { useNavigate } from "react-router-dom";
import useCanvaBehavior from "../path/to/useCanvaBehavior";
import "./Dashboard.css";

const courses = [/* your course data here */];

const Dashboard = () => {
  const navigate = useNavigate();

  const {
    scrollVelocity,
    hoverDuration,
    clickErrorRate,
    action,
    handleMouseEnter,
    handleMouseLeave,
    focusMode,
    focusedChatIds,
  } = useCanvaBehavior("dashboard-grid");

  const handleCourseClick = (course) => {
    navigate("/canvas/modules", { state: { course } });
  };

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-header">Dashboard</h2>

      <div className="dashboard-grid">
        {courses.map((course) => (
          <div
            key={course.id}
            className={`dashboard-card ${
              focusMode && !focusedChatIds.includes(course.id) ? "hidden-card" : ""
            }`}
            onClick={() => handleCourseClick(course)}
            onMouseEnter={() => handleMouseEnter(course.id)}
            onMouseLeave={handleMouseLeave}
          >
            <div className="card-top"></div>
            <div className="card-content">
              <h3>{course.title}</h3>
              <p>{course.subtitle}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Behavior tracking overlay (optional) */}
      <div className="tracking-panel">
        <p><b>Action:</b> {action}</p>
        <p><b>Scroll Velocity:</b> {scrollVelocity.toFixed(2)} px/s</p>
        <p><b>Hover Duration:</b> {hoverDuration}s</p>
        <p><b>Click Error Rate:</b> {clickErrorRate.toFixed(1)}%</p>
        <p><b>Focus Mode:</b> {focusMode ? "On" : "Off"}</p>
      </div>
    </div>
  );
};

export default Dashboard;
