import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Communities.css";

export default function Communities() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  const classes = [
    { id: 1, title: "Mathematics", teacher: "Prof. " },
    { id: 2, title: "Biology", teacher: "Dr. " },
    { id: 3, title: "History", teacher: "Mr. " },
    { id: 4, title: "Chemistry", teacher: "Ms. " },
    { id: 5, title: "Programming", teacher: "Engr. " },
    { id: 6, title: "Physics", teacher: "Dr. " },
    { id: 7, title: "Statistics", teacher: "Prof. " },
  ];

  const handleViewClick = (cls) => {
    navigate("/msteams/communities/activity", { state: { cls } });
  };

  // ðŸ”¹ Behavior Data Structure
  const updateBehaviorData = (type, details) => {
    const existing = JSON.parse(localStorage.getItem(type)) || {
      triggered: 0,
      history: [],
    };
    const updated = {
      ...existing,
      triggered: existing.triggered + 1,
      lastTrigger: new Date().toLocaleString(),
      history: [...existing.history, details],
    };
    localStorage.setItem(type, JSON.stringify(updated));
  };

  // ðŸ§  1. Scroll Velocity Tracker
  useEffect(() => {
    let lastScrollTop = 0;
    let lastTime = Date.now();
    const sidebar = document.querySelector(".ms-sidebar");
    const msContent = document.querySelector(".ms-content");

    const handleScroll = () => {
      const currentScrollTop = containerRef.current.scrollTop;
      const currentTime = Date.now();
      const deltaY = Math.abs(currentScrollTop - lastScrollTop);
      const deltaT = (currentTime - lastTime) / 1000; // seconds
      const velocity = deltaY / deltaT;

      lastScrollTop = currentScrollTop;
      lastTime = currentTime;

      if (velocity < 30 && sidebar) {
        sidebar.classList.add("hidden");
        msContent.classList.add("expanded");
        updateBehaviorData("scrollBehavior", { velocity });
        setTimeout(() => {
          sidebar.classList.remove("hidden");
          msContent.classList.remove("expanded");
        }, 10000);
      }
    };

    const ref = containerRef.current;
    ref?.addEventListener("scroll", handleScroll);
    return () => ref?.removeEventListener("scroll", handleScroll);
  }, []);

  // ðŸ§  2. Hover Duration Tracker
  useEffect(() => {
  let hoverTimer;
  let hideTimeout;

  const handleMouseEnter = (id) => {
    // start hover timer (3 seconds)
    hoverTimer = setTimeout(() => {
      setHoveredCard(id);
      updateBehaviorData("hoverBehavior", { cardId: id, activatedAt: new Date().toLocaleString() });
      document.body.classList.add("focus-mode");

      // hide all other cards
      const cards = document.querySelectorAll(".class-card");
      cards.forEach((card) => {
        if (card.dataset.id !== String(id)) {
          card.classList.add("hidden-card"); // hide non-hovered
        } else {
          card.classList.add("focused-card"); // emphasize hovered one
        }
      });

      // restore after 10 seconds
      hideTimeout = setTimeout(() => {
        document.body.classList.remove("focus-mode");
        setHoveredCard(null);
        cards.forEach((card) => {
          card.classList.remove("hidden-card", "focused-card");
        });
      }, 10000);
    }, 3000);
  };

  const handleMouseLeave = () => clearTimeout(hoverTimer);

  const cards = document.querySelectorAll(".class-card");
  cards.forEach((card) => {
    card.addEventListener("mouseenter", () => handleMouseEnter(card.dataset.id));
    card.addEventListener("mouseleave", handleMouseLeave);
  });

  return () => {
    clearTimeout(hoverTimer);
    clearTimeout(hideTimeout);
    cards.forEach((card) => {
      card.removeEventListener("mouseenter", handleMouseEnter);
      card.removeEventListener("mouseleave", handleMouseLeave);
    });
  };
}, []);

  // ðŸ§  3. Click Error Rate Tracker
  useEffect(() => {
    let totalClicks = 0;
    let invalidClicks = 0;

    const handleClick = (e) => {
      totalClicks++;
      const isValidClick = e.target.closest(".class-card") || e.target.closest(".class-view");

      if (!isValidClick) invalidClicks++;

      const errorRate = (invalidClicks / totalClicks) * 100;
      if (errorRate > 15) {
        updateBehaviorData("clickBehavior", { totalClicks, invalidClicks, errorRate });
        const container = document.querySelector(".class-grid-container");
        container.classList.add("enlarged");
        setTimeout(() => container.classList.remove("enlarged"), 10000);
      }
    };

    document.addEventListener("click", handleClick);
    return () => document.removeEventListener("click", handleClick);
  }, []);

  return (
    <div ref={containerRef} className="class-grid-container">
      {classes.map((cls) => (
        <div
          key={cls.id}
          data-id={cls.id}
          className={`class-card ${hoveredCard === cls.id ? "focused" : ""}`}
        >
          <div className="class-card-header"></div>
          <div className="class-card-body">
            <p className="class-title">
              Title: <span>{cls.title}</span>
            </p>
            <p className="class-teacher">
              Teacher: <span>{cls.teacher}</span>
            </p>
            <button className="class-view" onClick={() => handleViewClick(cls)}>
              view
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}
