import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Modules.css";
import useCanvaBehavior from "../useCanvaBehavior";

const Modules = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const course = location.state?.course;

  // ðŸ‘‡ from your behavior hook (you can use this later for sidebar/hide logic)
  useCanvaBehavior();

  // ðŸ‘‡ local focus state
  const [focusedItem, setFocusedItem] = useState(null);

  if (!course) {
    return (
      <div className="no-course">
        <p>No course selected.</p>
        <button onClick={() => navigate("/canvas/dashboard")}>
          Go Back to Dashboard
        </button>
      </div>
    );
  }

  const handleMouseEnter = (section, index) => {
    setFocusedItem(`${section}-${index}`);
  };

  const handleMouseLeave = () => {
    setFocusedItem(null);
  };

  return (
    <div className="subjects-container">
      <aside className="sidebar">
        <h2>Menu</h2>
        <ul>
          {[
            "Home",
            "Announcements",
            "Discussions",
            "Grades",
            "People",
            "Files",
            "Syllabus",
            "Modules",
          ].map((item) => (
            <li key={item}>{item}</li>
          ))}
        </ul>
      </aside>

      <main className="main-content">
        <h1 className="course-title">{course.title}</h1>

        {/* --- Assignments Section --- */}
        <section className="section">
          <h2>Assignments</h2>
          <div className="item-list">
            {course.assignments.map((assignment, index) => {
              const id = `assign-${index}`;
              const isFocused = focusedItem === id;
              const isHidden = focusedItem && focusedItem !== id;
              return (
                <div
                  key={index}
                  className={`item ${isFocused ? "focused-item" : ""} ${
                    isHidden ? "hidden-item" : ""
                  }`}
                  onMouseEnter={() => handleMouseEnter("assign", index)}
                  onMouseLeave={handleMouseLeave}
                >
                  {assignment}
                  {isFocused && (
                    <div className="focus-mode-label">Focus Mode Activate</div>
                  )}
                </div>
              );
            })}
          </div>
        </section>

        {/* --- Lessons Section --- */}
        <section className="section">
          <h2>Lessons</h2>
          <div className="item-list">
            {course.lessons.map((lesson, index) => {
              const id = `lesson-${index}`;
              const isFocused = focusedItem === id;
              const isHidden = focusedItem && focusedItem !== id;
              return (
                <div
                  key={index}
                  className={`item ${isFocused ? "focused-item" : ""} ${
                    isHidden ? "hidden-item" : ""
                  }`}
                  onMouseEnter={() => handleMouseEnter("lesson", index)}
                  onMouseLeave={handleMouseLeave}
                >
                  {lesson}
                  {isFocused && (
                    <div className="focus-mode-label">Focus Mode Activate</div>
                  )}
                </div>
              );
            })}
          </div>
        </section>
      </main>

      <aside className="right-panel">
        {[
          "View Course Stream",
          "Drop the Course",
          "View Course Calendar",
          "View Course Notification",
        ].map((btn, index) => (
          <button key={index} className="right-button">
            {btn}
          </button>
        ))}
      </aside>
    </div>
  );
};

export default Modules;
