import React from 'react' 
import './Home.css'
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const classes = [
    { id: 1, title: "IAS - CSB3 2ND SEMESTER 2025", teacher: "Dr. John Surveyor" },
    { id: 2, title: "THESIS | 2025", teacher: "Prof. Maria Santos" },
    { id: 3, title: "PROGRAMMING LANGUAGE", teacher: "Engr. Paul Dela Cruz" },
    { id: 4, title: "Math Elective", teacher: "Dr. Alan Tan" },
    { id: 5, title: "Web Development", teacher: "Ms. Carla Mendoza" },
    { id: 6, title: "Data Structures", teacher: "Mr. Noel Garcia" },
  ];

  const tasks = [
    { title: "Submit Act. 2", date: "May 25" },
    { title: "Read Chapter 3", date: "May 26" },
  ];

  const handleView = (index) => {
    navigate(`../class/${index}`);
  };

  return (
    <div className="home-contents">
      <div className="classes">
        {classes.map((cls,index) => (
          <div
            key={cls.id}
            id={`class-${cls.id}`} // ðŸ†” Add unique ID for each
            className="class-box"
            onMouseEnter={() => console.log(`Hovered on ${cls.title}`)}
            onMouseLeave={() => console.log(`Unhovered on ${cls.title}`)}
          >
            <h2>{cls.title}</h2>
            <button onClick={() => handleView(cls)}>View</button>
          </div>
        ))}
      </div>

      <div className="announcements">
        <div className='reminder-box'>
          <h2>Upcoming Tasks</h2>
          <ul className="task-list">
            {tasks.map((task, index) => (
              <li key={index}>
                <input type="checkbox" />
                <span>{task.title} - {task.date}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
