import React from "react";
import { useParams } from "react-router-dom";
import "./Classes.css";

const classesData = [
  {
    title: "IAS - CSB3 2ND SEMESTER 2025",
    teacher: "Dr. John Surveyor",
    announcements: [
      "New assignment posted: Research Paper",
      "Quiz scheduled next week."
    ],
    classwork: [
      { name: "Assignment 1", due: "Jan 25" },
      { name: "Midterm Exam", due: "Feb 10" },
    ],
    people: ["John Doe", "Jane Smith", "Chris Evans"]
  },
  {
    title: "THESIS | 2025",
    teacher: "Prof. Clara Bennett",
    announcements: ["Proposal defense schedule released."],
    classwork: [{ name: "Chapter 1 Draft", due: "March 1" }],
    people: ["Alice", "Bob", "Charlie"]
  },
];

export default function Classes() {
  const { id } = useParams();
  const classInfo = classesData[id];

  if (!classInfo) return <h2>Class not found</h2>;

  return (
    <div className="class-page">
      <div className="class-banner">
        <h1>{classInfo.title}</h1>
        <p>Instructor: {classInfo.teacher}</p>
      </div>

      <div className="tabs">
        <button>Stream</button>
        <button>Classwork</button>
        <button>People</button>
      </div>

      <div className="content">
        <h3>üì¢ Stream</h3>
        {classInfo.announcements.map((item, index) => (
          <p key={index}>‚Ä¢ {item}</p>
        ))}

        <h3>üìù Classwork</h3>
        {classInfo.classwork.map((cw, index) => (
          <p key={index}>{cw.name} ‚Äî Due: {cw.due}</p>
        ))}

        <h3>üë• People</h3>
        {classInfo.people.map((person, index) => (
          <p key={index}>{person}</p>
        ))}
      </div>
    </div>
  );
}
