import React from 'react'

export default function Chat() {
  const [messages, setMessages] = useState([
    {
      sender: "John",
      content: "Good morning everyone!",
      time: "7:32 AM",
    },
  ]);

  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;
    const newMessage = {
      sender: "You",
      content: input,
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
    };
    setMessages([...messages, newMessage]);
    setInput("");
  };

  return (
    <div className="chat-container">
      <div className="chat-sidebar">
        <div className="sidebar-header">
          <h2>Chat</h2>
          <div className="header-icons">
            <button>üìù</button>
            <button>üìû</button>
          </div>
        </div>

        <div className="chat-list">
          <div className="chat-item active">
            <div className="chat-avatar">üü£</div>
            <div className="chat-info">
              <h3>Temporary GC</h3>
              <p><strong>John:</strong> Good morning everyone!</p>
            </div>
          </div>
        </div>
      </div>

      <div className="chat-window">
        <div className="chat-header">
          <div className="chat-title">
            <div className="chat-avatar large">üü£</div>
            <h3>Temporary GC</h3>
          </div>
          <button className="menu-btn">‚ãÆ</button>
        </div>

        <div className="chat-messages">
          {messages.map((msg, i) => (
            <div key={i} className="chat-message">
              <span className="sender">{msg.sender}</span>
              <div className="message-bubble">{msg.content}</div>
              <span className="timestamp">{msg.time}</span>
            </div>
          ))}
        </div>

        <div className="chat-input-area">
          <div className="input-icons">
            <button>üìé</button>
            <button>üòä</button>
          </div>
          <input
            type="text"
            placeholder="Type a message"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
          />
          <button className="send-btn" onClick={handleSend}>‚û§</button>
        </div>
      </div>
    </div>
  )
}
