import React, { useEffect, useState } from "react";
import "./Help.css";

export default function Help() {
  const [behaviors, setBehaviors] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("userBehaviors")) || [];
    setBehaviors(stored);
  }, []);

  return (
    <div className="help-container">
      <h2>User Behavior Activity Log</h2>
      <p className="help-desc">
        Below is a temporary record of user interactions tracked during the session.
      </p>

      <table className="behavior-table">
        <thead>
          <tr>
            <th>Scroll Velocity (&lt;30px/s)</th>
            <th>Hover Duration (&lt;3s)</th>
            <th>Click Error Rate (&lt;15%)</th>
            <th>Date &amp; Time Recorded</th>
          </tr>
        </thead>
        <tbody>
          {behaviors.length === 0 ? (
            <tr>
              <td colSpan="4" style={{ textAlign: "center", color: "#777" }}>
                No behaviors recorded yet.
              </td>
            </tr>
          ) : (
            behaviors.map((b, index) => {
              const isScroll = b.key.includes("Scroll");
              const isHover = b.key.includes("Hover");
              const isClick = b.key.includes("Click");

              return (
                <tr key={index}>
                  <td>{isScroll ? b.value || "Triggered" : "-"}</td>
                  <td>{isHover ? b.value || "Triggered" : "-"}</td>
                  <td>{isClick ? b.value || "Triggered" : "-"}</td>
                  <td>{b.timestamp}</td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>

      <button
        className="clear-btn"
        onClick={() => {
          localStorage.removeItem("userBehaviors");
          setBehaviors([]);
        }}
      >
        Clear Behavior Log
      </button>
    </div>
  );
}
