import React, { useState, useEffect } from "react";
import useClassBehavior from "../useClassBehavior";
import useClassesBehavior from "../Gome/useClassesBehavior";
import "./Settings.css";

export default function Settings() {
  // Hooks for different tracked containers
  const classBehavior = useClassBehavior("class-container");
  const classesBehavior = useClassesBehavior("stream-container");

  const [storedData, setStoredData] = useState([]);

  // Load from localStorage in real-time
  useEffect(() => {
    const updateData = () => {
      const data = JSON.parse(localStorage.getItem("userBehaviors")) || [];
      setStoredData(data);
    };

    updateData();
    window.addEventListener("storage", updateData);
    return () => window.removeEventListener("storage", updateData);
  }, []);

  // Clear saved behavior data
  const handleClearData = () => {
    localStorage.removeItem("userBehaviors");
    setStoredData([]);
  };

  return (
    <div className="settings-container">
      <h2>User Behavior Settings</h2>

      {/* Live metrics from both hooks */}
      <div className="behavior-summary">
        <h3>ðŸ“Š Real-Time Behavior Data</h3>
        <div className="behavior-box">
          <p><strong>Scroll Velocity (Classes):</strong> {classBehavior.scrollVelocity.toFixed(1)}</p>
          <p><strong>Scroll Velocity (Streams):</strong> {classesBehavior.scrollVelocity.toFixed(1)}</p>
          <p><strong>Hover Duration:</strong> {Math.max(classBehavior.hoverDuration, classesBehavior.hoverDuration)}s</p>
          <p><strong>Click Error Rate:</strong> {(classBehavior.clickErrorRate || classesBehavior.clickErrorRate).toFixed(1)}%</p>
          <p><strong>Current Action:</strong> {classBehavior.action || classesBehavior.action}</p>
        </div>
      </div>

      {/* Saved history */}
      <div className="saved-data">
        <h3>ðŸ•’ Saved Behavior Logs</h3>
        {storedData.length === 0 ? (
          <p>No recorded behaviors yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Timestamp</th>
                <th>Action</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {storedData.map((entry, index) => (
                <tr key={index}>
                  <td>{entry.timestamp}</td>
                  <td>{entry.key}</td>
                  <td>{entry.value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* Clear Data */}
      <button className="clear-button" onClick={handleClearData}>
        ðŸ§¹ Clear All Data
      </button>
    </div>
  );
}
