import React from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  // ðŸŸ¢ Updated classes array â€” each class now has an id
  const classes = [
    { id: 0, title: "IAS - CSB3 2ND SEMESTER 2025" },
    { id: 1, title: "THESIS | 2025" },
    { id: 3, title: "PROGRAMMING LANGUAGE" },
    { id: 4, title: "Math Elective" },
    { id: 5, title: "Web Development" },
    { id: 6, title: "Data Structures" },
  ];

  const tasks = [
    { title: "Submit Act. 2", date: "May 25" },
    { title: "Read Chapter 3", date: "May 26" },
  ];

  const handleView = (cls) => {
    navigate(`../class/${cls.id}`, { state: { cls } });
  };

  return (
    <div className="home-contents">
      {/* ðŸŸ¢ Keep your original className="classes" */}
      <div className="classes">
        {classes.map((cls) => (
          <div
            key={cls.id}
            id={`class-${cls.id}`} // ðŸ†” Add unique ID for each
            className="class-box"
            onMouseEnter={() => console.log(`Hovered on ${cls.title}`)}
            onMouseLeave={() => console.log(`Unhovered on ${cls.title}`)}
          >
            <h2>{cls.title}</h2>
            <button onClick={() => handleView(cls)}>View</button>
          </div>
        ))}
      </div>

      {/* ðŸŸ¢ Your existing announcements section */}
      <div className="announcements">
        <div className="reminder-box">
          <h2>Upcoming Tasks</h2>
          <ul className="task-list">
            {tasks.map((task, index) => (
              <li key={index}>
                <input type="checkbox" />
                <span>
                  {task.title} - {task.date}
                </span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
