import React, { useEffect, useState } from "react";
import "./Help.css";

export default function Help() {
  const [behaviors, setBehaviors] = useState([]);

  // Load behaviors on mount
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("userBehaviors")) || [];
    setBehaviors(saved.reverse()); // newest first

    // Listen for storage updates (real-time sync across tabs)
    const handleStorage = (event) => {
      if (event.key === "userBehaviors") {
        const updated = JSON.parse(event.newValue) || [];
        setBehaviors(updated.reverse());
      }
    };
    window.addEventListener("storage", handleStorage);

    return () => window.removeEventListener("storage", handleStorage);
  }, []);

  return (
    <div className="help-container">
      <h2>User Behavior Records</h2>
      <table className="behavior-table">
        <thead>
          <tr>
            <th>Scroll velocity (&lt;30px/s)</th>
            <th>Hover Duration (&lt;3s)</th>
            <th>Click Error Rate (&lt;15%)</th>
            <th>Date and Time Recorded</th>
          </tr>
        </thead>
        <tbody>
          {behaviors.length > 0 ? (
            behaviors.map((b, index) => (
              <tr key={index}>
                <td>{b.key.includes("Scroll") ? b.value : "-"}</td>
                <td>{b.key.includes("Hover") ? b.value : "-"}</td>
                <td>{b.key.includes("Click Error Rate") ? b.value : "-"}</td>
                <td>{b.timestamp}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ textAlign: "center", opacity: 0.7 }}>
                No behavior records yet.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
