import { useState, useEffect, useRef, useCallback } from "react";

export default function useCanvaBehavior(containerClass = "class-container") {
  const [scrollVelocity, setScrollVelocity] = useState(0);
  const [hoverDuration, setHoverDuration] = useState(0);
  const [clickErrorRate, setClickErrorRate] = useState(0);
  const [focusMode, setFocusMode] = useState(false);
  const [action, setAction] = useState("Normal Layout");
  const [isDimmed, setIsDimmed] = useState(false);

  const hoverTimerRef = useRef(null);
  const totalClicksRef = useRef(0);
  const errorClicksRef = useRef(0);
  const clickModeTimeoutRef = useRef(null);
  const lastScrollTop = useRef(0);
  const lastScrollTime = useRef(Date.now());
  const userActiveTimeout = useRef(null);

  // ✅ Save Behavior to Backend
  const saveBehavior = useCallback(async (key, value) => {
    try {
      const token = localStorage.getItem("accessToken");
      const email = localStorage.getItem("userEmail");
      if (!token || !email) return;

      const participantId = localStorage.getItem("participantId");
      if (!participantId) return;

      const payload = {
        participant_id: participantId,
        scroll_velocity: scrollVelocity,
        hover_duration: `${hoverDuration}s`,
        click_error_rate: clickErrorRate,
        focus_mode: focusMode ? "Active" : "Inactive",
        action,
      };

      await fetch("http://127.0.0.1:8000/save-behavior/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });
    } catch (err) {
      console.error("Failed to save behavior:", err);
    }
  }, [scrollVelocity, hoverDuration, clickErrorRate, focusMode, action]);

  // ✅ Scroll Velocity Detection
  useEffect(() => {
    const container = document.querySelector(`.${containerClass}`);
    if (!container) return;

    const handleScroll = () => {
      const currentScrollTop = container.scrollTop;
      const now = Date.now();
      const distance = Math.abs(currentScrollTop - lastScrollTop.current);
      const timeElapsed = now - lastScrollTime.current;
      const velocity = (distance / timeElapsed) * 1000;

      setScrollVelocity(velocity);
      lastScrollTop.current = currentScrollTop;
      lastScrollTime.current = now;

      if (velocity > 15) {
        triggerEnlargeMode();
      }
    };

    container.addEventListener("scroll", handleScroll);
    return () => container.removeEventListener("scroll", handleScroll);
  }, [scrollVelocity, saveBehavior]);

  // ✅ Enlarge Mode Trigger
  const triggerEnlargeMode = useCallback(() => {
    const container = document.querySelector(`.${containerClass}`);
    if (!container) return;

    setAction("Enlarge Mode");
    saveBehavior("Scroll Velocity Trigger (>15px/s)", `${scrollVelocity.toFixed(2)}px/s`);
    container.classList.add("enlarged-mode");

    setTimeout(() => {
      container.classList.remove("enlarged-mode");
      setAction("Normal Layout");
    }, 2000);
  }, [containerClass, scrollVelocity, saveBehavior]);

  // ✅ Click Error Rate Tracking
  const triggerClickErrorMode = useCallback((rate) => {
    const container = document.querySelector(`.${containerClass}`);
    if (!container) return;

    setAction("Click Error Mode");
    saveBehavior("Click Error Rate Trigger (>15%)", `${rate.toFixed(1)}%`);
    container.classList.add("click-error-enlarged");

    clearTimeout(clickModeTimeoutRef.current);
    clickModeTimeoutRef.current = setTimeout(() => {
      container.classList.remove("click-error-enlarged");
      totalClicksRef.current = 0;
      errorClicksRef.current = 0;
      setClickErrorRate(0);
      setAction("Normal Layout");
    }, 10000);
  }, [containerClass, saveBehavior]);

  // ✅ Hover Duration Tracker
  useEffect(() => {
    const container = document.querySelector(`.${containerClass}`);
    if (!container) return;

    const handleMouseEnter = () => {
      hoverTimerRef.current = Date.now();
    };

    const handleMouseLeave = () => {
      if (hoverTimerRef.current) {
        const duration = (Date.now() - hoverTimerRef.current) / 1000;
        setHoverDuration(duration);
        saveBehavior("Hover Duration", `${duration.toFixed(2)}s`);
        hoverTimerRef.current = null;
      }
    };

    container.addEventListener("mouseenter", handleMouseEnter);
    container.addEventListener("mouseleave", handleMouseLeave);

    return () => {
      container.removeEventListener("mouseenter", handleMouseEnter);
      container.removeEventListener("mouseleave", handleMouseLeave);
    };
  }, [containerClass, saveBehavior]);

  // ✅ Click Tracking
  useEffect(() => {
    const handleClick = (e) => {
      totalClicksRef.current += 1;

      if (e.target.classList.contains("wrong-click")) {
        errorClicksRef.current += 1;
      }

      const rate = (errorClicksRef.current / totalClicksRef.current) * 100;
      setClickErrorRate(rate);

      if (rate > 15) triggerClickErrorMode(rate);
    };

    document.addEventListener("click", handleClick);
    return () => document.removeEventListener("click", handleClick);
  }, [triggerClickErrorMode]);

  // ✅ Focus Mode Tracking
  useEffect(() => {
    const handleFocus = () => setFocusMode(true);
    const handleBlur = () => setFocusMode(false);

    window.addEventListener("focus", handleFocus);
    window.addEventListener("blur", handleBlur);

    return () => {
      window.removeEventListener("focus", handleFocus);
      window.removeEventListener("blur", handleBlur);
    };
  }, []);

  useEffect(() => {
    saveBehavior("Focus Mode", focusMode ? "Active" : "Inactive");
  }, [focusMode, saveBehavior]);

  // ✅ User Inactivity → Dim Background (after 1 minute)
  useEffect(() => {
    const resetDimTimer = () => {
      setIsDimmed(false);
      if (userActiveTimeout.current) clearTimeout(userActiveTimeout.current);
      userActiveTimeout.current = setTimeout(() => {
        setIsDimmed(true);
        saveBehavior("UI Dimming", "Inactive for 1 minute");
      }, 60000);
    };

    const events = ["mousemove", "keydown", "click", "scroll"];
    events.forEach((ev) => window.addEventListener(ev, resetDimTimer));

    resetDimTimer();

    return () => {
      events.forEach((ev) => window.removeEventListener(ev, resetDimTimer));
      if (userActiveTimeout.current) clearTimeout(userActiveTimeout.current);
    };
  }, [isDimmed, saveBehavior]);

  // ✅ Save Action Changes
  useEffect(() => {
    if (action) {
      saveBehavior("Action", action);
    }
  }, [action, saveBehavior]);

  return {
    scrollVelocity,
    hoverDuration,
    clickErrorRate,
    focusMode,
    action,
    isDimmed,
  };
}
